
module.exports = function () {
  var SomeView = mojo.views.Base.extend({
    react: React.createClass({
      render: function () {
        return React.DOM.h3(null, "Hello "+this.props.name+"!");
      }
    })
  });


  var view = new SomeView({ name: "Craig"});
  var doc = view.render();
  document.body.appendChild(doc);


  var SomeMojoView = mojo.views.Base.extend({
    paper: require("text!test.pc")
  });


  var reactView = mojo.react.createClass(SomeMojoView);

  React.renderComponent(reactView({message:"craig"}), document.body);
}
